<?phpdeclare(strict_types = 1);/*+-----------------------------------------------------------------------------------------------------------------------|+-----------------------------------------------------------------------------------------------------------------------| 拓展 laravel-modules 包的命令|*/namespace App\Console\Commands;use Illuminate\Console\Command;use Illuminate\Filesystem\Filesystem;use App\Traits\Commands\ScaffoldCommand;class ServiceMakeCommand extends Command{    use ScaffoldCommand;    /**     * 控制台命令 signature 的名称。     *     * @var string     */    protected $signature = 'module:make-service {name} {module}';    /**     * 控制台命令说明     *     * @var string     */    protected $description = '创建指定模块的 Service 文件';    /**     * @var string 创建的文件类型     */    protected $defaultFileType = 'service';    /**     * HandlerMakeCommand constructor.     *     * @param Filesystem $files     */    public function __construct(Filesystem $files)    {        parent::__construct();        // 储存文件类对象        $this->files = $files;    }    /**     * 执行命令     *     * @throws \Illuminate\Contracts\Filesystem\FileNotFoundException     */    public function handle()    {        // 设置从命令行键入的值        $this->setArguments($this->arguments());        // 输出成功信息到控制台        $this->info('create success ' . $this->startMake());    }}