<?phpdeclare(strict_types = 1);/*+-----------------------------------------------------------------------------------------------------------------------|+-----------------------------------------------------------------------------------------------------------------------| 生成 handler 业务处理器文件|*/namespace App\Console\Commands\create;use Illuminate\Filesystem\Filesystem;use App\Traits\Commands\ScaffoldCommand;use App\Console\Commands\CreateScaffoldCommand;class CreateHandler extends CreateAbstract{    use ScaffoldCommand;    /**     * @var CreateScaffoldCommand     */    protected $command;    /**     * @var string 创建的文件类型     */    protected $defaultFileType = 'handler';    /**     * CreateHandler constructor.     *     * @param CreateScaffoldCommand $command     * @param Filesystem $files     */    public function __construct(CreateScaffoldCommand $command, Filesystem $files)    {        $this->command = $command;    }    /**     * 执行命令     *     * @throws \Illuminate\Contracts\Filesystem\FileNotFoundException     */    protected function start()    {        // 设置从命令行键入的值        $this->setArguments($this->command->arguments());        // 输出成功信息到控制台        $this->command->info('create success ' . $this->startMake());    }}