<?phpdeclare(strict_types = 1);/*+-----------------------------------------------------------------------------------------------------------------------| Author: 植成樑 <atzcl0310@gmail.com>  Blog：https://www.atzcl.cn+-----------------------------------------------------------------------------------------------------------------------| 每个处理具体业务功能点需要实现的接口类|*/namespace App\Foundation\Interfaces;interface HandlerInterface{    /**     * 前置需要调用方法集合     *     * @return array     */    public function beforeExecutes(): array;    /**     * 业务逻辑处理     *     * @return mixed     * @throws \Exception     */    public function execute();    /**     * 后置需要调用方法集合     *     * @return array     */    public function afterExecutes(): array;}