<?phpdeclare(strict_types = 1);/*+-----------------------------------------------------------------------------------------------------------------------|+-----------------------------------------------------------------------------------------------------------------------| 处理登录|*/namespace Modules\User\Handlers\V1;use Modules\User\Traits\AuthorizeTrait;use Modules\User\Http\Requests\LoginRequest;use App\Foundation\Abstracts\HandlerAbstract;use Modules\User\Repositories\UserRepository;class Login extends HandlerAbstract{    use LoginRequest, AuthorizeTrait;    /**     * Login constructor.     *     * @param UserRepository $repository     */    public function __construct(UserRepository $repository)    {        parent::__construct();        $this->repository = $repository;    }    /**     * 处理登录     *     * @return $this|mixed|self     */    public function execute()    {        // 验证请求数据        $this->executeValidate();        // 获取登录结果        $result = $this->attemptLogin();        // 更多操作... [ 签发 jwt、判断登录权限、api 资源转换等等 ]        $this->setStatusData($result);    }}